import{_ as a,o as l,c as e,Q as t}from"./chunks/framework.e7363dcb.js";const b=JSON.parse('{"title":"图论杂项","description":"","frontmatter":{},"headers":[],"relativePath":"Graph/图论杂项.md","filePath":"Graph/图论杂项.md","lastUpdated":1740448168000}'),r={name:"Graph/图论杂项.md"},i=t('<h1 id="图论杂项" tabindex="-1">图论杂项 <a class="header-anchor" href="#图论杂项" aria-label="Permalink to &quot;图论杂项&quot;">​</a></h1><h2 id="平面图相关" tabindex="-1">平面图相关 <a class="header-anchor" href="#平面图相关" aria-label="Permalink to &quot;平面图相关&quot;">​</a></h2><h3 id="平面图定义" tabindex="-1">平面图定义 <a class="header-anchor" href="#平面图定义" aria-label="Permalink to &quot;平面图定义&quot;">​</a></h3><p>能画在平面上，边与边除端点外不相交的图。</p><h3 id="相关概念" tabindex="-1">相关概念 <a class="header-anchor" href="#相关概念" aria-label="Permalink to &quot;相关概念&quot;">​</a></h3><ul><li>无限面：即外部面。</li><li>有限面：即内部面。</li><li>面的次数：面边界的边数。</li></ul><h3 id="重要推论" tabindex="-1">重要推论 <a class="header-anchor" href="#重要推论" aria-label="Permalink to &quot;重要推论&quot;">​</a></h3><p>平面图所有面的次数和等于其度数的两倍。</p><h3 id="极大平面图" tabindex="-1">极大平面图 <a class="header-anchor" href="#极大平面图" aria-label="Permalink to &quot;极大平面图&quot;">​</a></h3><ul><li><strong>定义</strong>：多连任何一条边都不合法的图。</li><li><strong>定理</strong>：一张图是极大平面图等价于其所有面次数都是 3。</li></ul><h3 id="欧拉公式" tabindex="-1">欧拉公式 <a class="header-anchor" href="#欧拉公式" aria-label="Permalink to &quot;欧拉公式&quot;">​</a></h3><p>$V - E + F = 2$，适用于平面图和多边形。</p><h3 id="重要结论" tabindex="-1">重要结论 <a class="header-anchor" href="#重要结论" aria-label="Permalink to &quot;重要结论&quot;">​</a></h3><ul><li>平面图的边数不超过 $3\\times n - 6$。 <ul><li><strong>proof</strong>：文档未给出具体证明内容。</li></ul></li><li>平面图中至少存在一个度数不超过 5 的结点。 <ul><li><strong>proof</strong>：反证即可。</li></ul></li></ul><h3 id="对偶图" tabindex="-1">对偶图 <a class="header-anchor" href="#对偶图" aria-label="Permalink to &quot;对偶图&quot;">​</a></h3><ul><li><strong>定义</strong>：每个面作为结点，两个点有边等价于两个面相邻。</li><li><strong>性质</strong>： <ul><li>平面图最小割 = 对偶图最小环。由上述引理可知，平面图最小割 &lt;= 5。</li><li>平面图 $x$ 和 $y$ 之间的最小割 = 对偶图中最小的包围 $x$ 或 $y$ 的环。在网格图中，可以将环在无限面处拆开，从而变成最短路。</li></ul></li></ul><h2 id="偏序集相关" tabindex="-1">偏序集相关 <a class="header-anchor" href="#偏序集相关" aria-label="Permalink to &quot;偏序集相关&quot;">​</a></h2><ul><li><strong>传递闭包</strong>：刻画可达性的 01 矩阵。</li><li><strong>偏序集</strong>：传递闭包就是其本身的 DAG。</li><li><strong>dilworth 引理</strong>：偏序集的最小路径覆盖 = 其最大独立集。这里的路径覆盖可以是相交或不相交。</li></ul><h2 id="简单应用" tabindex="-1">简单应用 <a class="header-anchor" href="#简单应用" aria-label="Permalink to &quot;简单应用&quot;">​</a></h2><ul><li>CSP - S 2024 决斗</li><li>导弹拦截 plus</li></ul>',20),o=[i];function n(h,s,d,u,c,_){return l(),e("div",null,o)}const q=a(r,[["render",n]]);export{b as __pageData,q as default};
